const calculateProductionTimeCutKomax = (capabilityProperties, orderSize ) => {
    let wireMaterialSetup = 5 /*min */ * 60 /* sec */ * 1000 /* msec */;
    let orderRelease = 1 /*min */ * 60 /* sec */ * 1000 /* msec */;
    let setupTimePerPiece = Math.ceil((wireMaterialSetup+orderRelease) / orderSize);
    let productionTimePerPiece = Number(capabilityProperties.WireNominalLength) * 2;
    return setupTimePerPiece + productionTimePerPiece;
}

const calculateProductionTimeStripKomax = (capabilityProperties, orderSize ) => {
    let orderRelease = 1 /*min */ * 60 /* sec */ * 1000 /* msec */;
    let setupTimePerPiece = Math.ceil((orderRelease) / orderSize);
    let productionTimePerPiece = Number(capabilityProperties.NominalStrippingLength) * 5;
    return setupTimePerPiece + productionTimePerPiece;
}

const calculateProductionTimeCrimpKomax = (capabilityProperties, orderSize, toolSetupNeeded) => {
    let terminalMaterialSetup = 5 /*min */ * 60 /* sec */ * 1000 /* msec */;
    let orderRelease = 5 /*min */ * 60 /* sec */ * 1000 /* msec */;
    if (toolSetupNeeded )
    {
        terminalMaterialSetup += 8 /*min */ * 60 /* sec */ * 1000 /* msec */;
    }
    let setupTimePerPiece = Math.ceil((terminalMaterialSetup + orderRelease) / orderSize);
    let productionTimePerPiece = 300;
    return setupTimePerPiece + productionTimePerPiece;
}

const calculateProductionTimeCrimpWezag = (capabilityProperties, orderSize, toolSetupNeeded) => {
    let terminalMaterialSetup = 0;
    let orderRelease = 5 /*min */ * 60 /* sec */ * 1000 /* msec */;
    if (toolSetupNeeded) {
        terminalMaterialSetup += 8 /*min */ * 60 /* sec */ * 1000 /* msec */;
    }
    let setupTimePerPiece = Math.ceil((terminalMaterialSetup + orderRelease) / orderSize);
    let productionTimePerPiece = 20000;
    return setupTimePerPiece + productionTimePerPiece;
}



const calculateProductionTime = (msg, capabilityIdShortPath, machineAasId, toolSetupNeeded) => {
    let orderSize = 500;
    let capabilityType = msg.capabilityTypes[ capabilityIdShortPath ];
    // Demonstator has to Machine Types, KOMAX and WEZAG
    // This Node Red Flow tales over the logic for both types
    // Better identifcaion provided by AAS - Demo purpose only!
    if( machineAasId.toUpperCase().indexOf("KOMAX") > -1 )
    {
        switch (capabilityType )
        {
            case "CutCapability":
                return calculateProductionTimeCutKomax(msg.capabilityProperties[capabilityIdShortPath], orderSize );
            case "StripCapability":
                return calculateProductionTimeStripKomax(msg.capabilityProperties[capabilityIdShortPath], orderSize);
            case "CrimpCapability":
                return calculateProductionTimeCrimpKomax(msg.capabilityProperties[capabilityIdShortPath], orderSize, toolSetupNeeded);
            default:
                return 999999;
        }
    }
    else
    {
        switch (capabilityType) {
            case "CrimpCapability":
                return calculateProductionTimeCrimpWezag(msg.capabilityProperties[capabilityIdShortPath], toolSetupNeeded);
            default:
                return 999999;
        }
    }
}

