            }
        }
    }
    capabilityTypes[ capabilitySetIdShort+"/"+capabilityContainer.idShort ] = capabilityType;
    capabilityProperties[capabilitySetIdShort+"/"+capabilityContainer.idShort] = propertySet;
}
